def malumot(key, val):    malumotlar = {}    malumotlar[key] = val    return malumotlar    def set_info():    l = []    while True:        print("Q m kiriting.")        key = input("key>>> ")        val = input("value>>> ")        l.append(malumot(key, val))        sav = input("m q?  (1|0)")        if sav == '0':            break    return l