import random as rdef son_c(x=10):    print(f"1 dan {x} gacha son o'ylang \va istalgan tugmani bosing.", end=" ")    input("Men topaman.")    while True:        taxmin_son = r.randint(1, x)        ishora = input(f"siz {taxmin_son} \sonini o'yladingiz: to'g'ri (t),men \o'ylagan son bundan kattaroq (+), \yoki kichikroq (-)")        if ishora == 't':            print('T')            break                    